import { BASE_PATH } from '../utils/constants';
import { authFetch } from '../utils/fetch';

export async function createAdressApi(adress, logout) {
    try {
        const url = `${BASE_PATH}/adresses`;
        const params = {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(adress),
        };
        const result = await authFetch(url, params, logout);
        return result;
    } catch (error) {
        console.log(error)
        return null;
    }
}

export async function getAdressesApi(idUser, logout) {
    try {
        const url = `${BASE_PATH}/adresses?user=${idUser}`;
        const result = await authFetch(url, null, logout);
        if(result.statusCode === 5000) throw "Error del servidor"
        return result;
    } catch (error) {
        console.log(error);
        return null;
    }
}
