import { useState, useEffect } from 'react';
import { getAdressesApi } from '../../../api/adress';
import useAuth from '../../../hooks/useAuth';

export default function ListAdress() {
    const [adresses, setAdresses] = useState(null);
    const { auth, logout } = useAuth();
    console.log(adresses)

    useEffect(() => {
        (async () => {
            const response = await getAdressesApi(auth.idUser, logout);
            setAdresses(response || []);
        })()
    }, []);

    return (
        <div>
            <h1>lista de direcciones</h1>
        </div>
    )
}
