import { Form, Button } from 'semantic-ui-react';

export default function ChangeNameForm({ user }) {
    return (
        <div className="change-name-form">
            <h4>Edita tus datos</h4>
            <Form>
                <Form.Group widths="equal">
                    <Form.Input name="name" placeholder="Nuevo Nombre"  />
                    <Form.Input name="lastname" placeholder="Nuevo Apellido"  />
                </Form.Group>
                <Button className="submit" >Actualizar</Button>
            </Form>
        </div>
    )
}
