import { useFormik } from 'formik';
import { Form, Button } from 'semantic-ui-react';
import * as Yup from 'yup';
import { toast } from 'react-toastify';

export default function ChangeNameForm({ user }) {
    const formik = useFormik({
        initialValues: initialValues(user.name, user.lastname),
    })

    return (
        <div className="change-name-form">
            <h4>Edita tus datos</h4>
            <Form onSubmit={formik.handleSubmit}>
                <Form.Group widths="equal">
                    <Form.Input name="name" placeholder="Nuevo Nombre" onChange={formik.handleChange} value={formik.values.name} />
                    <Form.Input name="lastname" placeholder="Nuevo Apellido" onChange={formik.handleChange} value={formik.values.lastname} />
                </Form.Group>
                <Button className="submit" >Actualizar</Button>
            </Form>
        </div>
    )
}

function initialValues(name, lastname) {
    return {
        name: name || "" ,
        lastname: name || ""
    };
};
