import { useState } from 'react';
import { Form, Button } from 'semantic-ui-react';
import { useFormik, yupToFormErrors } from 'formik'
import * as Yup from 'yup';
import { toast } from 'react-toastify'


export default function LoginForm({ showRegisterForm }) {
const formik = useFormik({
    initialValues: initialValues(),
    validationSchema: Yup.object(validationSchema())

})

    return (
        <Form className="login-form">
            <Form.Input name="identifier"
                type="text"
                placeholder="Correo Electronico"
            />
             <Form.Input name="password"
                type="password"
                placeholder="Contraseña"
            />
            <div className="actions">
                <Button type="button" basic onClick={showRegisterForm}>Registrarse</Button>
                <div>
                    <Button className="submit" type="submit">Iniciar Sesión</Button>
                    <Button type="button">¿Has olvidado la contraseña?</Button>
                </div>
            </div>
        </Form>
    )
}

function initialValues() {
return {
    identifier:"",
    password:""
}
}

function validationSchema(){
    return {
        identifier: Yup.string().email(true).required(true),
        password: Yup.string().required(true)
    }

}
