import {useState} from 'react';
import { Form, Button } from 'semantic-ui-react'
import { useFormik } from 'formik';
import * as Yup from 'yup';

export default function ChangeEmailForm({ user, logout, setReloadUser }) {
    const [loading, setLoading] = useState(false);

    const formik = useFormik({
        initialValues: initialValues(),
        validationSchema: Yup.object(validationSchema()),
        onSubmit: async (formData) => {
            setLoading(true);
            const response = await u
        }
    });
    return (
        <div className="change-email-form">
            <h4>Cambia tu e-mail <span>(Tu e-mail actual :{user.email} )</span></h4>
            <Form onSubmit={formik.handleSubmit}>
                <Form.Group widths="equal">
                    <Form.Input
                        name="email"
                        placeholder="Nuevo Email"
                        onChange={formik.handleChange}
                        value={formik.values.email}
                        error={formik.errors.email}
                    />
                    <Form.Input
                        name="repeatEmail"
                        placeholder="Confirma tu nuevo Email"
                        onChange={formik.handleChange}
                        value={formik.values.repeatEmail}
                        error={formik.errors.repeatEmail}
                    />
                </Form.Group>
                <Button className="submit" >Actualizar</Button>
            </Form>
        </div>
    )
}

function initialValues() {
    return {
        email: "",
        repeatEmail: ""
    }
}

function validationSchema() {
    return {
        email: Yup.string().required(true),
        repeatEmail: Yup.string().required(true)
    }
}
