import { Form, Button } from 'semantic-ui-react'
import { useFormik } from 'formik';
import * as Yup from 'yup';

export default function AdressForm() {

    const formik = useFormik({
        initialValues: initialValues(),
        validationSchema: Yup.object(validationSchema()),
        onSubmit: async (formData) => {
            console.log(formData)
        }
    });
    return (
        <Form onSubmit={formik.handleSubmit}>
            <Form.Input
                name="title"
                type="text"
                label="Titulo de la direccion"
                placeholder="Titulo de la direcci贸n"
                onChange= {formik.handleChange}
                value ={formik.values.titile}
                error={formik.errors.titile}
            />

            <Form.Group widths="equal">
                <Form.Input
                    name="name"
                    type="text"
                    label="Nombre y Apellido"
                    placeholder="Nombre y Apellido"
                    onChange= {formik.handleChange}
                    value ={formik.values.name}
                    error={formik.errors.name}
                />
                <Form.Input
                    name="adress"
                    type="text"
                    label="Direccion"
                    placeholder="Direccion"
                    onChange= {formik.handleChange}
                    value ={formik.values.adress}
                    error={formik.errors.adress}
                />
            </Form.Group>
            <Form.Group widths="equal">
                <Form.Input
                    name="city"
                    type="text"
                    label="Ciudad"
                    placeholder="Ciudad"
                    onChange= {formik.handleChange}
                    value ={formik.values.city}
                    error={formik.errors.city}
                />
                <Form.Input
                    name="state"
                    type="text"
                    label="Estado/Provincia/Region"
                    placeholder="Estado/Provincia/Region"
                    onChange= {formik.handleChange}
                    value ={formik.values.state}
                    error={formik.errors.state}
                />
            </Form.Group>
            <Form.Group widths="equal">
                <Form.Input
                    name="postalCode"
                    type="text"
                    label="C贸digo postal"
                    placeholder="C贸digo postal"
                    onChange= {formik.handleChange}
                    value ={formik.values.postalCode}
                    error={formik.errors.postalCode}
                />
                <Form.Input
                    name="phone"
                    type="text"
                    label="Numero de telefono"
                    placeholder="Numero de telefono"
                    onChange= {formik.handleChange}
                    value ={formik.values.phone}
                    error={formik.errors.phone}
                />
            </Form.Group>
            <div className="actions">
                <Button className="submit" type="submit" >
                    Crear direcci贸n
                </Button>
            </div>
        </Form>
    )
}


function initialValues() {
    return {
        titile: "",
        name: "",
        adress: "",
        city: "",
        state: "",
        postalCode: "",
        phone: ""
    }
};

function validationSchema() {
    return {
        title: Yup.string().required(true),
        name: Yup.string().required(true),
        adress: Yup.string().required(true),
        city: Yup.string().required(true),
        state: Yup.string().required(true),
        postalCode: Yup.string().required(true),
        phone: Yup.string().required(true),
    }
};
